import React, {useState, useCallback} from 'react';
import styled from 'styled-components';
import palette from "../../lib/styles/palette";

const TagBoxBlock = styled.div`
    width: 100%;
    border-top: 1px solid ${palette.gray[2]};
    padding-top: 2rem;

    h4{
    color: ${palette.gray[8]};
    margin-top: 0;
    margin-bottom: 0.5rem;
    }
`;

const TagForm = styled.div`
    border-radius: 4px;
    overflow: hidden;
    display:flex;
    width: 256pxx;
    border: 1px solid ${palette.gray[9]};/*스타일 초기화*/
    input,
    button{
    outline: none;
    border:none;
    font-size: 1rem;
    }

    input{
    padding:0.5rem;
    flex: 1;
    }

    button {
    cursor: pointer;
    padding-right: 1rem;
    padding-left: 1rem;
    border: none;
    background: ${palette.gray[8]};
    color:white;
    font-weight: bold;
    &:hover{
    background: ${palette.gray[6]};
    }
}    
`;

const Tag = styled.div`
    margin-right: 0.5rem;
    color: ${palette.gray[6]};
    cursor: pointer;
    &:hover {
    opacity: 0.5;
    }
`;

const TagListBlock = styled.div`
  display: flex;
  margin-top: 0.5rem;
`;

//react.memo사용하여 tag값이 변경시에만 리렌러링
const TagItem = React.memo(({tag, onRemove}) => <Tag onClick={() => onRemove(tag)}>#{tag}</Tag>);

const TagList = React.memo(({ tags, onRemove }) => (
    <TagListBlock>
        {tags.map(tag => (
            <TagItem key={tag} tag={tag} onRemove={onRemove} />
        ))}
    </TagListBlock>
));

const TagBox = () => {
    const [input, setInput] = useState('');
    const [localTags, setLocalTags] = useState([]);

    const insertTag = useCallback(
        tag => {
            if (!tag) return;
            if (localTags.includes(tag)) return;//존재하는 경우 추가X
            setLocalTags([...localTags, tag]);
        },
        [localTags],
    );

    const onRemove = useCallback(
        tag => {
            setLocalTags(localTags.filter(t => t !== tag));
        },
        [localTags],
    );

    const onChange = useCallback(e => {
        setInput(e.target.value);
    }, []);

    const onSubmit = useCallback(
        e => {
            e.preventDefault();
            insertTag(input.trim());
            setInput('');
        },
        [input, insertTag]
    );

    return (
        <TagBoxBlock>
            <h4>태그</h4>
            <TagForm onSubmit={onSubmit}>
                <input placeholder={"태그를 입력하세요"}
                       value={input} onChange={onChange}/>
                <button type={"submit"}>추가</button>
            </TagForm>
            <TagList tags={[localTags]} onRemove={onRemove}/>
        </TagBoxBlock>
    )
};

export default TagBox;
